private static void createDatabase() throws SQLException {
        String stmt1 = "CREATE DATABASE IF NOT EXISTS  `StudentInformationSystem`;";
        String stmt2 = "use StudentInformationSystem;";      
        String stmt5 = "create table IF NOT EXISTS `Students` (AdmNo varchar(20),RollNo int,Name varchar (30),dob varchar (10),gender varchar (7),fname varchar (20),std varchar (10),addr varchar (40),PhNo varchar (12),bgroup varchar(5),house varchar (10));";
        
        statement = connection.createStatement();

        statement.executeUpdate(stmt1);
        statement.executeUpdate(stmt2);
        statement.executeUpdate(stmt5);

    }

