    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        boolean pass=true;
        String entr=new String(tf.getPassword());
        password=entr;
        try {
            DriverManager.setLoginTimeout(23);
            connection = DriverManager.getConnection(DATABASE_URL, "root", password);

            createDatabase();
        } catch (SQLException ex) {
            JOptionPane.showMessageDialog (null,"Incorrect Password !!");
            pass=false;
        } finally {

            try {
                if (connection != null) {
                    connection.close();
                }
                if (statement != null) {
                    statement.close();
                }
            } catch (SQLException ex) {
                JOptionPane.showMessageDialog (null,"Error in Data Connectivity !!");
            }
        }
    
        Menu ob=new Menu ();
        if (pass)
        {
            JOptionPane.showMessageDialog (null,"Login Successful !! Press Ok to continue");
            this.setVisible (false);
            ob.setVisible (true);
        }
    }//GEN-LAST:event_jButton1ActionPerformed

